document.addEventListener('DOMContentLoaded',function(){let course={'ERG':'682','Neta':'1'};const inputValue=document.getElementById('value'),selectCurrencyOne=document.getElementById('curr_one'),selectCurrencyTwo=document.getElementById('curr_two'),inputResult=document.getElementsByClassName('result-input')[0];function summ(){let z=0;if(selectCurrencyOne.value===selectCurrencyTwo.value){inputResult.innerText=inputValue.value}else{if(selectCurrencyOne.value!='Neta'||selectCurrencyOne.value!='ERG'){z=inputValue.value*course[selectCurrencyOne.value];inputResult.innerHTML=Math.ceil((z/course[selectCurrencyTwo.value])*100)/100}else{inputResult.innerHTML=Math.ceil((inputValue.value*course[selectCurrencyTwo.value])*100)/100}}};inputValue.oninput=function(){summ()};selectCurrencyOne.onchange=function(){summ()};selectCurrencyTwo.onchange=function(){summ()}})